# ligne très importante qui appelle la gem.
require 'twitter'

require 'dotenv'# Appelle la gem Dotenv
require 'pry'

Dotenv.load('.env') # Ceci appelle le fichier .env (situé dans le même dossier que celui d'où tu exécute app.rb)
 # et grâce à la gem Dotenv, on importe toutes les données enregistrées dans un hash ENV
 

# quelques lignes qui appellent les clés d'API de ton fichier .env
client = Twitter::REST::Client.new do |config|
  config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
  config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
  config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
  config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
end
# p client


# binding.pry # On lance PRY au milieu de la méthode
# client.update('Mon premier tweet en Ruby !!!!')

list = ["DelphineCuny"] #Ici, j'ai préféré ne pas trop mettre de journalistes, 10 pour éviter les bans et autres

=begin

#Fonction pour Favorite (Liker) les recents tweet d'une liste de journaliste:

list.each do |x|		#On sait comment chercher les dernier Tweets d'un utilisateur, ainsi on va appliquer un each sur la liste pour chaque 
	tweet_id = client.user_timeline(x) #Fetch les tweet les plus recent (par IDs) d'un user en utilisant la methode user.timeline
	puts (tweet_id) #Affiche les IDs des tweets, afin de pouvoir vérifier si notre code s'execute bien dans le bash
	client.favorite(tweet_id) #Like les tweets qui ont le tweed_id avec la methode client.favorite.
end

=end


#Fonction pour follow une liste de personne, ici quelque jorunalistes:

list.each do |x|
	client.follow(x)
end

